import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import Carousel from "react-bootstrap/Carousel";
import b1 from '../asserts/b1.jpg';
import b2 from '../asserts/b2.jpg';
import b3 from '../asserts/b3.jpg';


const EcommerceLanding = () => {
  return (
    <div className="container-fluid">
      <div className="row">
        {/* Sidebar */}
        <div className="col-md-3 bg-light p-3">
          <h5 className="text-danger fw-bold">&#9776; Shop By Categories</h5>
          <ul className="list-group list-group-flush">
            {[
              "Women",
              "Men",
              "Children",
              "Tops",
              "Bottoms",
              "Shoes",
              "Bags",
              "Accessories",
              "Others",
            ].map((category) => (
              <li key={category} className="list-group-item">
                {category}
              </li>
            ))}
          </ul>
        </div>

        {/* Main Banner Carousel */}
        <div className="col-md-9 d-flex align-items-center justify-content-center bg-light">
          <Carousel>
            {[b1,b2,b3].map((img,index) => (
              <Carousel.Item key={index}>
                <div className="text-center p-5">
                  <p className="text-danger">NEW ARRIVAL</p>
                  <h1>Fall - Winter 2021</h1>
                  <button className="btn btn-outline-dark mt-3">Shop Now</button>
                  {console.log(index)}
                </div>
                <img
                  src={img}
                  alt={`Slide ${index}`}
                  className="d-block w-100"
                />
              </Carousel.Item>
            ))}
          </Carousel>
        </div>
      </div>
    </div>
  );
};

export default EcommerceLanding;
