import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Cart = () => {
    const [cart, setCart] = useState([]);
    const [orders, setOrders] = useState([]);
    const user = JSON.parse(localStorage.getItem("user"));

    useEffect(() => {
        if (user && user._id) {
            axios.get(`http://localhost:3685/api/cart/${user._id}`)
                .then(response => setCart(response.data))
                .catch(error => console.error("Error fetching cart data:", error));
            
            axios.get(`http://localhost:3685/api/orders`)
                .then(response => setOrders(response.data.orders))
                .catch(error => console.error("Error fetching orders:", error));
        }
    }, [user]);

    const removeItem = (itemId) => {
        axios.delete(`http://localhost:3685/api/cart/${user._id}/${itemId}`)
            .then(() => {
                setCart(cart.filter(item => item._id !== itemId));
            })
            .catch(error => console.error("Error removing item:", error));
    };

    return (
        <div style={{ padding: "20px", maxWidth: "600px", margin: "auto" }}>
            <h2 style={{ textAlign: "center", marginBottom: "20px" }}>Shopping Cart</h2>
            {cart.length > 0 ? (
                <ul style={{ listStyle: "none", padding: 0 }}>
                    {cart.map((item) => (
                        <li key={item._id} style={{ 
                            border: "1px solid #ddd", 
                            padding: "15px", 
                            marginBottom: "10px", 
                            borderRadius: "8px", 
                            display: "flex", 
                            justifyContent: "space-between", 
                            alignItems: "center",
                            background: "#f9f9f9"
                        }}>
                            <div>
                                <h3 style={{ margin: 0 }}>{item.title}</h3>
                                <p style={{ margin: "5px 0" }}>Price: ₹{item.price}</p>
                                <p style={{ fontSize: "14px", color: "#555" }}>{item.description}</p>
                            </div>
                            <button 
                                onClick={() => removeItem(item._id)} 
                                style={{ 
                                    background: "#ff4d4d", 
                                    color: "white", 
                                    border: "none", 
                                    padding: "8px 12px", 
                                    borderRadius: "5px", 
                                    cursor: "pointer", 
                                    fontSize: "14px"
                                }}
                            >
                                Remove
                            </button>
                        </li>
                    ))}
                </ul>
            ) : (
                <p style={{ textAlign: "center", fontSize: "18px", color: "#777" }}>Your cart is empty.</p>
            )}

            <h2 style={{ textAlign: "center", marginTop: "30px" }}>Your Orders</h2>
            {orders.length > 0 ? (
                <ul style={{ listStyle: "none", padding: 0 }}>
                    {orders.map((order) => (
                        <li key={order._id} style={{ 
                            border: "1px solid #ddd", 
                            padding: "15px", 
                            marginBottom: "10px", 
                            borderRadius: "8px", 
                            background: "#f1f1f1"
                        }}>
                            <h3>Order ID: {order._id}</h3>
                            <p>Shipping Address: {order.shippingadd}</p>
                            <p>Total Amount: ₹{order.totalamount}</p>
                            <p>Status: {order.status}</p>
                            <p>Payment Method: {order.payment}</p>
                        </li>
                    ))}
                </ul>
            ) : (
                <p style={{ textAlign: "center", fontSize: "18px", color: "#777" }}>No orders found.</p>
            )}
        </div>
    );
};

export default Cart;
