import React, { useState } from 'react';
import axios from 'axios';

const Login = ({ setIsLoggedIn }) => {
  const [isRegister, setIsRegister] = useState(false);
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    username: '',
    phnno: ''
  });

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const url = isRegister ? 'http://localhost:3685/api/register' : 'http://localhost:3685/api/login';
      const response = await axios.post(url, formData);

      if (!isRegister) {
        localStorage.setItem('user', JSON.stringify(response.data.data)); // Store user data
        setIsLoggedIn(true);
      } else {
        alert('Registration successful! Please login.');
        setIsRegister(false);
        setFormData({ email: '', password: '', username: '', phnno: '' }); // Reset form
      }
    } catch (error) {
      console.error('Error:', error.response?.data || error.message);
      alert('Error: ' + (error.response?.data?.message || 'Something went wrong'));
    }
  };

  return (
    <div className="container">
      <div className="image-section"></div>
      <div className="form-section">
        {isRegister ? (
          <form onSubmit={handleSubmit} className="form-style">
            <h2>Create Account</h2>
            <input type="email" name="email" placeholder="Email" required value={formData.email} onChange={handleInputChange} />
            <input type="text" name="username" placeholder="Username" required value={formData.username} onChange={handleInputChange} />
            <input type="password" name="password" placeholder="Password" required value={formData.password} onChange={handleInputChange} />
            <input type="tel" name="phnno" placeholder="Phone Number" required value={formData.phnno} onChange={handleInputChange} />
            <button type="submit">Register</button>
            <div className="toggle-form">
              Already have an account? <span onClick={() => setIsRegister(false)}>Login</span>
            </div>
          </form>
        ) : (
          <form onSubmit={handleSubmit} className="form-style">
            <h2>Welcome Back</h2>
            <input type="email" name="email" placeholder="Email" required value={formData.email} onChange={handleInputChange} />
            <input type="password" name="password" placeholder="Password" required value={formData.password} onChange={handleInputChange} />
            <button type="submit">Login</button>
            <div className="toggle-form">
              Don't have an account? <span onClick={() => setIsRegister(true)}>Register</span>
            </div>
          </form>
        )}
      </div>
    </div>
  );
};

export default Login;
