// components/Navbar.jsx
import React from 'react';
import './Navbar.css';
import { useState } from 'react';
import { Link } from "react-router-dom";


const Navbar = ({ setSelectedCategory }) => {
  const categories = ['all', 'electronics', 'clothing', 'books', 'home'];
  const [isOpen, setIsOpen] = useState(false);

  return (
    <nav className="navbar1">
      <div className='navh1'>
        <div className='headericons'>
          <div className='iconcon'>
            <i class="bi bi-instagram"></i>
            <i class="bi bi-facebook"></i>
            <i class="bi bi-linkedin"></i>
            <i class="bi bi-snapchat"></i>
            <i class="bi bi-whatsapp"></i>
          </div>
        </div>
        <div className='headerspace'></div>
        <div className='headeroptions'>
          <Dropdown title="Language" options={["English", "French"]} />
          <div className="divider"></div> {/* Divider */}
          <Dropdown title="Currency" options={["INR (₹)", "USD ($)"]} />
          <div className="divider"></div> {/* Divider */}
          <a href="#" className="login">logout</a>
        </div>
      </div>
      <div className='navh2'>
          <div className="logo"></div>
          <div className="headersearchbar"></div>
          <div className="carticons"></div>
      </div>
      <div><nav>
        <Link to="/home">Home</Link> |
         <Link to="/about">About</Link>
         <Link to="/product">Products</Link>

      </nav></div>
    </nav>
  );
};
function Dropdown({ title, options }) {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <div
      className="dropdown"
      onMouseEnter={() => setIsOpen(true)}
      onMouseLeave={() => setIsOpen(false)}
    >
      <button className="dropbtn">{title} ▼</button>
      {isOpen && (
        <div className="dropdown-content">
          {options.map((option, index) => (
            <a key={index} href="#">{option}</a>
          ))}
        </div>
      )}
    </div>
  );
}
export default Navbar;
