// components/Navbar.jsx
import React from 'react';
import './Navbar.css';


const Navbar = ({ setSelectedCategory }) => {
  const categories = ['all', 'electronics', 'clothing', 'books', 'home'];

  return (
    <nav className="navbar">
      <div className='navh1'>
        <div className='headericons'>
          <div className='iconcon'>
            <i class="bi bi-instagram"></i>
            <i class="bi bi-facebook"></i>
            <i class="bi bi-linkedin"></i>
            <i class="bi bi-snapchat"></i>
            <i class="bi bi-whatsapp"></i>
          </div>
        </div>
        <div className='headerspace'></div>
        <div className='headeroptions'>
        <div
      className="relative inline-block"
      onMouseEnter={() => setIsOpen(true)}
      onMouseLeave={() => setIsOpen(false)}
    >
      {/* Dropdown Button */}
      <button className="text-red-600 font-semibold flex items-center">
        Options ‚ñº
      </button>

      {/* Dropdown Menu */}
      {isOpen && (
        <div className="absolute left-0 mt-2 w-40 bg-white border rounded shadow-md">
          {/* Language Section */}
          <div className="px-4 py-2 text-gray-700 font-bold">üåç Language</div>
          <a href="#" className="block px-4 py-2 hover:bg-gray-200">
            English
          </a>
          <a href="#" className="block px-4 py-2 hover:bg-gray-200">
            French
          </a>

          <hr />

          {/* Currency Section */}
          <div className="px-4 py-2 text-gray-700 font-bold">üí∞ Currency</div>
          <a href="#" className="block px-4 py-2 hover:bg-gray-200">
            INR (‚Çπ)
          </a>
          <a href="#" className="block px-4 py-2 hover:bg-gray-200">
            USD ($)
          </a>

          <hr />

          {/* Logout */}
          <a href="#" className="block px-4 py-2 text-red-600 hover:bg-gray-200 font-bold">
            üîí Logout
          </a>
        </div>
      )}
    </div>
        </div>
      </div>
      <div className='navh2'>

      </div>
    </nav>
  );
};

export default Navbar;
