import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Button, Modal, Form } from 'react-bootstrap';
import './ManageProducts.css';

const ManageProducts = () => {
  const [products, setProducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [showEditModal, setShowEditModal] = useState(false);
  const [showAddModal, setShowAddModal] = useState(false);
  const [newProduct, setNewProduct] = useState({
    title: '',
    price: '',
    description: '',
    img: '',
    stock: '',
    category: ''
  });

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = () => {
    axios.get('http://localhost:3685/api/products')
      .then(response => setProducts(response.data))
      .catch(error => console.error('Error fetching products:', error));
  };

  const handleEdit = (product) => {
    setSelectedProduct(product);
    setShowEditModal(true);
  };

  const handleSaveEdit = () => {
    axios.put(`http://localhost:3685/api/products/${selectedProduct._id}`, selectedProduct)
      .then(() => {
        fetchProducts();
        setShowEditModal(false);
      })
      .catch(error => console.error('Error updating product:', error));
  };

  const handleDelete = (productId) => {
    if (window.confirm("Are you sure you want to delete this product?")) {
      axios.delete(`http://localhost:3685/api/products/${productId}`)
        .then(() => fetchProducts())
        .catch(error => console.error('Error deleting product:', error));
    }
  };

  const handleAddProduct = () => {
    axios.post('http://localhost:3685/api/products', newProduct)
      .then(() => {
        fetchProducts();
        setShowAddModal(false);
        setNewProduct({ title: '', price: '', description: '', img: '', stock: '', category: '' });
      })
      .catch(error => console.error('Error adding product:', error));
  };

  return (
    <div className="manage-products-container">
      <div className="manage-products-header">
        <h2>Manage Products</h2>
        <Button className="add-product-btn" onClick={() => setShowAddModal(true)}>+ Add Product</Button>
      </div>

      <div className="product-list">
        {products.map(product => (
          <div key={product._id} className="product-card">
            <img src={product.img} alt={product.title} className="product-img" />
            <div className="product-info">
              <h4 className="product-title">
                {product.title.length > 30 ? `${product.title.substring(0, 30)}...` : product.title}
              </h4>
              <p className="product-desc">
                {product.description.length > 100 ? `${product.description.substring(0, 100)}...` : product.description}
              </p>
              <p className="product-price">₹{product.price}</p>
            </div>
            <div className="product-actions">
              <Button className="edit-btn" onClick={() => handleEdit(product)}>Edit</Button>
              <Button className="delete-btn" onClick={() => handleDelete(product._id)}>Delete</Button>
            </div>
          </div>
        ))}
      </div>

      {/* Edit Product Modal */}
      <Modal show={showEditModal} onHide={() => setShowEditModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Edit Product</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {selectedProduct && (
            <Form>
              <Form.Group>
                <Form.Label>Title</Form.Label>
                <Form.Control type="text" value={selectedProduct.title}
                  onChange={(e) => setSelectedProduct({ ...selectedProduct, title: e.target.value })} />
              </Form.Group>
              <Form.Group>
                <Form.Label>Price</Form.Label>
                <Form.Control type="number" value={selectedProduct.price}
                  onChange={(e) => setSelectedProduct({ ...selectedProduct, price: e.target.value })} />
              </Form.Group>
              <Form.Group>
                <Form.Label>Description</Form.Label>
                <Form.Control as="textarea" value={selectedProduct.description}
                  onChange={(e) => setSelectedProduct({ ...selectedProduct, description: e.target.value })} />
              </Form.Group>
              <Form.Group>
                <Form.Label>Image URL</Form.Label>
                <Form.Control type="text" value={selectedProduct.img}
                  onChange={(e) => setSelectedProduct({ ...selectedProduct, img: e.target.value })} />
              </Form.Group>
            </Form>
          )}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowEditModal(false)}>Close</Button>
          <Button variant="primary" onClick={handleSaveEdit}>Save Changes</Button>
        </Modal.Footer>
      </Modal>

      {/* Add Product Modal */}
      <Modal show={showAddModal} onHide={() => setShowAddModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Add New Product</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group>
              <Form.Label>Title</Form.Label>
              <Form.Control type="text" value={newProduct.title}
                onChange={(e) => setNewProduct({ ...newProduct, title: e.target.value })} />
            </Form.Group>
            <Form.Group>
              <Form.Label>Price</Form.Label>
              <Form.Control type="number" value={newProduct.price}
                onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })} />
            </Form.Group>
            <Form.Group>
              <Form.Label>Description</Form.Label>
              <Form.Control as="textarea" value={newProduct.description}
                onChange={(e) => setNewProduct({ ...newProduct, description: e.target.value })} />
            </Form.Group>
            <Form.Group>
              <Form.Label>Image URL</Form.Label>
              <Form.Control type="text" value={newProduct.img}
                onChange={(e) => setNewProduct({ ...newProduct, img: e.target.value })} />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowAddModal(false)}>Close</Button>
          <Button variant="primary" onClick={handleAddProduct}>Add Product</Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default ManageProducts;
