import React, { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";

const ProductList = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:3685/api/products")
      .then(response => setProducts(response.data))
      .catch(error => console.error("Error fetching products:", error));
  }, []);

  const handleAddToCart = (productId) => {
    console.log(`Product ${productId} added to cart (API not ready)`);
  };

  const truncateDescription = (description) => {
    if (!description || description.length < 20) {
      console.error("Description is too short or missing");
      return "Description not available";
    }
    return description.length > 20 ? description.substring(0, 20) + "..." : description;
  };

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4">Products</h2>
      <div className="row">
        {products.map(product => (
          <div key={product.id} className="col-md-4 mb-4">
            <div className="card shadow-sm h-100">
              <img src={product.img} className="card-img-top" alt={product.title} style={{ height: "250px", objectFit: "cover" }} />
              <div className="card-body d-flex flex-column">
                <h5 className="card-title">{product.title}</h5>
                <p className="card-text">{truncateDescription(product.description)}</p>
                <h6 className="text-primary">${product.price}</h6>
                <button className="btn btn-dark mt-auto" onClick={() => handleAddToCart(product.id)}>
                  Add to Cart
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductList;
